import{_ as a,c as t,o as r,ag as o}from"./chunks/framework.C4nOkCZI.js";const u=JSON.parse('{"title":"Who is LaravelUi5 for?","description":"","frontmatter":{"head":[["link",{"rel":"canonical","href":"https://laravelui5.com/guide/target-audience.html"}]]},"headers":[],"relativePath":"guide/target-audience.md","filePath":"guide/target-audience.md"}'),n={name:"guide/target-audience.md"};function i(s,e,l,d,c,p){return r(),t("div",null,[...e[0]||(e[0]=[o('<h1 id="who-is-laravelui5-for" tabindex="-1">Who is LaravelUi5 for? <a class="header-anchor" href="#who-is-laravelui5-for" aria-label="Permalink to &quot;Who is LaravelUi5 for?&quot;">​</a></h1><p><strong>LaravelUi5</strong> is designed for developers and teams who want to build modern business applications by combining the power of Laravel on the backend with the enterprise-grade UI capabilities of OpenUI5 on the frontend.</p><div class="warning custom-block"><p class="custom-block-title">UI Power × Backend Simplicity</p><p>SAP UI5 is a best-in-class enterprise frontend framework —<br> Laravel is a best-in-class modern PHP application framework.</p></div><p>Bringing them together in a clean, SAP-compliant way unlocks major value for modern <strong>SAP customers</strong>, <strong>SMBs</strong>, and <strong>custom business platforms</strong>.</p><p>It’s a great fit for:</p><ul><li><strong>Laravel developers</strong> who need to build complex, data-driven UIs that go beyond what Blade and traditional frontend stacks easily support.</li><li><strong>Teams familiar with SAP Fiori or OpenUI5</strong> who want to leverage their UI5 skills without relying on the full SAP ecosystem (e.g., SAP BTP or Fiori Launchpad).</li><li><strong>Agencies and consulting firms</strong> looking to deliver SAP-like UX standards in custom web applications — fast and without vendor lock-in.</li><li><strong>CTOs and architects</strong> aiming to scale their Laravel applications with clean separation of concerns, maintainable frontend code, and modular deployment strategies.</li><li><strong>SaaS builders</strong> who want a robust, standards-based UI framework integrated with a flexible and battle-tested Laravel backend.</li></ul><p>Whether you’re working on internal tools, client-facing dashboards, or full-scale enterprise software, LaravelUi5 helps you bring structure, modularity, and a proven UI standard to your frontend stack.</p>',7)])])}const h=a(n,[["render",i]]);export{u as __pageData,h as default};
